mapper.select:
select
  hotel_Id, hotel_Name, star_Level, phone_Number, address, price, Icon
from hotel


        and hotel_Id = #{hotelId}


        and hotel_Name = #{hotelName}


        and star_Level = #{starLevel}


        and phone_Number = #{phoneNumber}


        and address = #{address}


        and price = #{price}


        and Icon = #{icon}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from hotel


        and hotel_Id = #{hotelId}


        and hotel_Name = #{hotelName}


        and star_Level = #{starLevel}


        and phone_Number = #{phoneNumber}


        and address = #{address}


        and price = #{price}


        and Icon = #{icon}
