mapper.select:
select
  performance_Id, scenic_Area_Id, performance_Name, team_Name, performance_Type, Icon, time
from performance


        and performance_Id = #{performanceId}


        and scenic_Area_Id = #{scenicAreaId}


        and performance_Name = #{performanceName}


        and team_Name = #{teamName}


        and performance_Type = #{performanceType}


        and Icon = #{icon}


        and time = #{time}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from performance


        and performance_Id = #{performanceId}


        and scenic_Area_Id = #{scenicAreaId}


        and performance_Name = #{performanceName}


        and team_Name = #{teamName}


        and performance_Type = #{performanceType}


        and Icon = #{icon}


        and time = #{time}
