mapper.select:
select
  order_Id, order_description, visitor_id, hotel_id, result
from room_order


        and order_Id = #{orderId}


        and order_description = #{orderDescription}


        and visitor_id = #{visitorId}


        and hotel_id = #{hotelId}


        and result = #{result}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from room_order


        and order_Id = #{orderId}


        and order_description = #{orderDescription}


        and visitor_id = #{visitorId}


        and hotel_id = #{hotelId}


        and result = #{result}
