mapper.select:
select
  scenic_Area_Id, scenic_Area_Name, phone_Number, Icon_1, Icon_2, Icon_3, description
from scenic_area


        and scenic_Area_Id = #{scenicAreaId}


        and scenic_Area_Name = #{scenicAreaName}


        and phone_Number = #{phoneNumber}


        and Icon_1 = #{icon1}


        and Icon_2 = #{icon2}


        and Icon_3 = #{icon3}


        and description = #{description}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from scenic_area


        and scenic_Area_Id = #{scenicAreaId}


        and scenic_Area_Name = #{scenicAreaName}


        and phone_Number = #{phoneNumber}


        and Icon_1 = #{icon1}


        and Icon_2 = #{icon2}


        and Icon_3 = #{icon3}


        and description = #{description}

select scenic_area.scenic_Area_Id, scenic_Area_Name, phone_Number, Icon_1, Icon_2, Icon_3, description
from nearly,scenic_area
where nearly.scenic_Area_Id=scenic_area.scenic_Area_Id and nearly.hotel_Id=#{hotelId}
